execution.script:
pipeline {
    agent any

    environment {
        PYTHONUNBUFFERED = "1"
        HEADLESS         = "true"
        WDM_LOCAL        = "1"
        WDM_CACHE        = "${WORKSPACE}/.wdm"
        HOME             = "${WORKSPACE}"
        PYTHONPATH       = "${WORKSPACE}:${PYTHONPATH}"

        REPORT_DIR       = "reports"
        SCREENSHOT_DIR   = "screenshots"
        DOCKER_IMAGE     = "elice-bugshunters"
    }

    stages {
        stage('Checkout') {
            steps {
                checkout scm
            }
        }

        stage('Build Docker Image') {
            steps {
                script {
                    sh '''
                        set -eux
                        echo "üê≥ Docker Ïù¥ÎØ∏ÏßÄ ÎπåÎìú ÏãúÏûë"
                        docker builder prune -f || true
                        docker build -t ${DOCKER_IMAGE}:${BUILD_NUMBER} -f Dockerfile .
                        docker tag ${DOCKER_IMAGE}:${BUILD_NUMBER} ${DOCKER_IMAGE}:latest
                        docker images | head
                    '''
                }
            }
        }

        stage('Run Tests in Container') {
            steps {
                withCredentials([
                    usernamePassword(
                        credentialsId: 'login-id',
                        usernameVariable: 'MAIN_EMAIL',
                        passwordVariable: 'MAIN_PASSWORD'
                    ),
                    usernamePassword(
                        credentialsId: 'sub-id',
                        usernameVariable: 'SUB_EMAIL',
                        passwordVariable: 'SUB_PASSWORD'
                    )
                ]) {
                    sh '''
                        set -eux

                        echo "‚û° PWD:"
                        pwd
                        echo "‚û° WORKSPACE:"
                        echo "$WORKSPACE"

                        REPORT_DIR_HOST="$WORKSPACE/${REPORT_DIR}"
                        REPORT_DIR_CONT="/app/${REPORT_DIR}"
                        SCREENSHOT_DIR_HOST="$WORKSPACE/${SCREENSHOT_DIR}"
                        SCREENSHOT_DIR_CONT="/app/${SCREENSHOT_DIR}"

                        echo "üßπ Í∏∞Ï°¥ Î¶¨Ìè¨Ìä∏/Ïä§ÌÅ¨Î¶∞ÏÉ∑ Ï†ïÎ¶¨"
                        rm -rf "$REPORT_DIR_HOST" "$SCREENSHOT_DIR_HOST"
                        mkdir -p "$REPORT_DIR_HOST" "$SCREENSHOT_DIR_HOST"

                        echo "üê≥ ÌÖåÏä§Ìä∏ Ïª®ÌÖåÏù¥ÎÑà Ïã§Ìñâ (pytest)"
                        docker run --rm \
                          --shm-size=2g \
                          -e HEADLESS=true \
                          -e WDM_SKIP=1 \
                          -e CHROME_BIN=/usr/bin/chromium \
                          -e CHROMEDRIVER=/usr/bin/chromedriver \
                          -e MAIN_EMAIL="$MAIN_EMAIL" \
                          -e MAIN_PASSWORD="$MAIN_PASSWORD" \
                          -e SUB_EMAIL="$SUB_EMAIL" \
                          -e SUB_PASSWORD="$SUB_PASSWORD" \
                          -v "$REPORT_DIR_HOST:$REPORT_DIR_CONT" \
                          -v "$SCREENSHOT_DIR_HOST:$SCREENSHOT_DIR_CONT" \
                          ${DOCKER_IMAGE}:latest \
                          tests -v \
                            --junitxml=${REPORT_DIR_CONT}/test-results.xml \
                            --html=${REPORT_DIR_CONT}/report.html \
                            --self-contained-html \
                            --tb=short

                        echo "üìÇ docker run Ïù¥ÌõÑ Î¶¨Ìè¨Ìä∏ ÎîîÎ†âÌÜ†Î¶¨ ÎÇ¥Ïö©:"
                        ls -lah "$REPORT_DIR_HOST" || true

                        echo "üìÇ docker run Ïù¥ÌõÑ Ïä§ÌÅ¨Î¶∞ÏÉ∑ ÎîîÎ†âÌÜ†Î¶¨ ÎÇ¥Ïö©:"
                        ls -lah "$SCREENSHOT_DIR_HOST" || true
                    '''
                }
            }
        }
    }

    post {
        always {
            script {
                echo "üì¶ JUnit Î¶¨Ìè¨Ìä∏ ÏàòÏßë ÏãúÎèÑ"
                try {
                    // WORKSPACE Í∏∞Ï§Ä: reports/test-results.xml
                    junit 'reports/test-results.xml'
                } catch (err) {
                    echo "JUnit Î¶¨Ìè¨Ìä∏Î•º Ï∞æÏßÄ Î™ªÌñàÏäµÎãàÎã§: ${err}"
                }

                echo "üìä HTML Î¶¨Ìè¨Ìä∏ Í≤åÏãú ÏãúÎèÑ"
                try {
                    publishHTML(target: [
                        allowMissing:           true,
                        alwaysLinkToLastBuild:  true,
                        keepAll:                true,
                        reportDir:              'reports',
                        reportFiles:            'report.html',
                        reportName:             'Pytest HTML Report'
                    ])
                } catch (err) {
                    echo "HTML Î¶¨Ìè¨Ìä∏Î•º Í≤åÏãúÌïòÏßÄ Î™ªÌñàÏäµÎãàÎã§: ${err}"
                }

                def reportExists = fileExists('reports/report.html')
                echo "Î¶¨Ìè¨Ìä∏ ÌååÏùº Ï°¥Ïû¨: ${reportExists}"

                if (reportExists) {
                    archiveArtifacts(
                        artifacts: 'reports/**/*,screenshots/**/*',
                        allowEmptyArchive: true,
                        fingerprint: true
                    )
                    echo "‚úÖ Î¶¨Ìè¨Ìä∏/Ïä§ÌÅ¨Î¶∞ÏÉ∑ ÏïÑÏπ¥Ïù¥Î∏å ÏôÑÎ£å"
                    echo "üìä HTML Î¶¨Ìè¨Ìä∏: ${BUILD_URL}artifact/reports/report.html"
                } else {
                    echo "‚ùå Î¶¨Ìè¨Ìä∏ ÌååÏùºÏù¥ ÏÉùÏÑ±ÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§"
                    echo "ÌÖåÏä§Ìä∏Í∞Ä Ïã§Ìå®ÌñàÍ±∞ÎÇò pytest-html ÌîåÎü¨Í∑∏Ïù∏Ïù¥ ÏóÜÏùÑ Ïàò ÏûàÏäµÎãàÎã§"
                }
            }

            // ÏÑ†ÌÉù: Docker ÏûêÏõê Ï†ïÎ¶¨ (ÏõêÏπò ÏïäÏúºÎ©¥ Ï£ºÏÑù Ï≤òÎ¶¨)
            sh 'docker system prune -f || true'
        }
    }
}
